		let th = document.createElement('th')
		tr.appendChild(th)

		// CREATE TABLE HEADER INFORMATION
		for (let i = 0; i < this.event.times.length; i++) {
			for (let k = 0; k < this.event.times[i].length; k++) {
				if(k == 0) {
					let date_cell = document.createElement('th')
